import React, { useState, useEffect } from 'react';
import './App.css';
import MapComponent from './MapComponent';

// The backend API endpoint
const API_URL = 'http://localhost:5000/api/reports';

function App() {
  // State for the list of reports
  const [reports, setReports] = useState([]);
  // State for the form inputs
  const [groupName, setGroupName] = useState('');
  const [activityType, setActivityType] = useState('Food Distribution');
  const [description, setDescription] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [darkMode, setDarkMode] = useState(false);

  // Fetch reports from the backend when the component mounts
  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => setReports(data))
      .catch((error) => console.error("Error fetching reports:", error));
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Geocode city, state to lat/lng
    const geocodeUrl = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(city)},${encodeURIComponent(state)},USA&limit=1`;
    
    try {
      const geoResponse = await fetch(geocodeUrl);
      const geoData = await geoResponse.json();
      
      if (geoData.length === 0) {
        alert('Location not found. Please check city and state.');
        return;
      }

      const newReport = {
        groupName,
        activityType,
        description,
        location: {
          lat: parseFloat(geoData[0].lat),
          lng: parseFloat(geoData[0].lon),
          city,
          state
        },
      };

    // Send the new report to the backend
    fetch(API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newReport),
    })
      .then((res) => res.json())
      .then((savedReport) => {
        // Add the new report to the top of the list
        setReports([savedReport, ...reports]);
        // Clear form fields
        setGroupName('');
        setDescription('');
        setCity('');
        setState('');
      })
      .catch((error) => console.error("Error submitting report:", error));
    } catch (error) {
      console.error("Error geocoding location:", error);
      alert('Error finding location. Please try again.');
    }
  };

  return (
    <div className={`App ${darkMode ? 'dark-mode' : ''}`}>
      <header className="App-header">
        <h1>Mutual Aid Activity Mapper</h1>
        <button className="dark-mode-toggle" onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? '‚òÄÔ∏è' : 'üåô'}
        </button>
      </header>
      <main>
        <div className="report-form">
          <h2>Report an Activity</h2>
          <form onSubmit={handleSubmit}>
            <input type="text" value={groupName} onChange={(e) => setGroupName(e.target.value)} placeholder="Group Name" required />
            <select value={activityType} onChange={(e) => setActivityType(e.target.value)}>
              <option>Food Distribution</option>
              <option>Shelter</option>
              <option>Medical Supplies</option>
              <option>Clothing Drive</option>
              <option>Other</option>
            </select>
            <input type="text" value={city} onChange={(e) => setCity(e.target.value)} placeholder="City" required />
            <select value={state} onChange={(e) => setState(e.target.value)} required>
              <option value="">Select State</option>
              <option value="AL">Alabama</option>
              <option value="AK">Alaska</option>
              <option value="AZ">Arizona</option>
              <option value="AR">Arkansas</option>
              <option value="CA">California</option>
              <option value="CO">Colorado</option>
              <option value="CT">Connecticut</option>
              <option value="DE">Delaware</option>
              <option value="FL">Florida</option>
              <option value="GA">Georgia</option>
              <option value="HI">Hawaii</option>
              <option value="ID">Idaho</option>
              <option value="IL">Illinois</option>
              <option value="IN">Indiana</option>
